import java.util.*;

public class Main {

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int m = sc.nextInt();
        int d = sc.nextInt();
        int sum = 0;
        int[] c = new int[m];
        for (int i = 0; i < m; i++) {
            c[i] = sc.nextInt();
            sum += c[i];
        }
        if (sum > n || (m + 1) * (d - 1) + sum < n) {
            System.out.println("NO");
        } else {
            System.out.println("YES");
            int target = n - sum;
            int[] arr = new int[n];
            int index = 0;
            int mIndex = 0;
            while (mIndex < c.length) {
                if (target - d - 1 >= 0) {
                    index += d - 1;
                    target -= d - 1;
                } else {
                    index += Math.max(0, target - d + 1);
                    target = 0;
                }
                for (int i = 0; i < c[mIndex]; i++) {
                    arr[index++] = mIndex + 1;
                }
                mIndex++;
            }
            for (int i : arr) {
                System.out.print(i + " ");
            }
            System.out.println();
        }
    }
}

