    private void solveB() throws IOException {
        int n = in.nextInt();
        int[] p = in.nextIntArray(n);

        boolean[] used = new boolean[n + 1];
        for (int l = 0, r = 0, next = 1; l < n; l = r) {
            while (p[r] != next)
                r++;

            if (l == r) {
                used[next++] = true;
                r++;
            } else {
                for (int i = r; i > l; i--)
                    p[i] = p[i - 1];
                p[l] = next++;
                for (int i = l; i < r; i++)
                    used[p[i]] = true;
            }
            while (next <= n && used[next])
                next++;
        }

        for (int i = 0; i < n; i++)
            out.print(p[i] + " ");
        out.println();
