def cross(n, m, d, c):
    if (m+1)*(d-1) + sum(c) < n:
        return 'NO'
    ans = []
    space = 1
    space_left = n - sum(c)
    c_count = 0
    while space_left:
        if space < d:
            ans.append('0')
            space += 1
            space_left -= 1
        else:
            ans.extend([c_count+1 for i in range(c[c_count])])
            c_count += 1
            space = 1
    while c_count < m:
        ans.extend([c_count+1 for i in range(c[c_count])])
        c_count += 1
    return ('YES', ' '.join([str(i) for i in ans]))


