public class Main {
  private static void execute(ContestReader reader, PrintWriter out) {
    int n = reader.nextInt();
    int m = reader.nextInt();
    int d = reader.nextInt();
    int[] cs = reader.nextIntArray(m);
    for (String line : new Solver(n, m, d, cs).solve()) {
      out.println(line);
    }
  }

  public static void main(String[] args) {
    ContestReader reader = new ContestReader(System.in);
    PrintWriter out = new PrintWriter(System.out);
    execute(reader, out);
    out.flush();
  }
}

class Solver {
  final int n, m, d;
  final int[] cs;

  Solver(int n, int m, int d, int[] cs) {
    this.n = n;
    this.m = m;
    this.d = d;
    this.cs = cs;
  }

  public List<String> solve() {
    int sumC = 0;
    for (int c : cs) {
      sumC += c;
    }
    int countWater = n - sumC;
    int maxJump = countWater / (m + 1);
    if (countWater % (m + 1) != 0) {
      maxJump++;
    }
    if (maxJump > d - 1) {
      return Arrays.asList("NO");
    }


    List<Integer> waters = new ArrayList<>();
    for (int i = 0; i <= m; i++) {
      waters.add(countWater / (m + 1) + (i < countWater % (m + 1) ? 1 : 0));
    }

    List<Integer> assignment = new ArrayList<>();
    for (int i = 0; true; i++) {
      for (int j = 0; j < waters.get(i); j++) {
        assignment.add(0);
      }
      if (i == m) {
        break;
      }
      for (int j = 0; j < cs[i]; j++) {
        assignment.add(i + 1);
      }
    }

    StringBuffer sb = new StringBuffer();
    sb.append(assignment.get(0));
    for (int i = 1; i < assignment.size(); i++) {
      sb.append(" ");
      sb.append(assignment.get(i));
    }
    return Arrays.asList("YES", sb.toString());
  }
}

class ContestReader {
  private BufferedReader reader;
  private StringTokenizer tokenizer;

  ContestReader(InputStream in) {
    reader = new BufferedReader(new InputStreamReader(in));
  }

  public String next() {
    while (tokenizer == null || !tokenizer.hasMoreTokens()) {
      try {
        tokenizer = new java.util.StringTokenizer(reader.readLine());
      } catch (Exception e) {
        throw new RuntimeException(e);
      }
    }
    return tokenizer.nextToken();
  }

  public int nextInt() {
    return Integer.parseInt(next());
  }

  public long nextLong() {
    return Long.parseLong(next());
  }

  public double nextDouble() {
    return Double.parseDouble(next());
  }

  public String[] nextArray(int n) {
    String[] array = new String[n];
    for (int i = 0; i < n; i++) {
      array[i] = next();
    }
    return array;
  }

  public int[] nextIntArray(int n) {
    int[] array = new int[n];
    for (int i = 0; i < n; i++) {
      array[i] = nextInt();
    }
    return array;
  }

  public long[] nextLongArray(int n) {
    long[] array = new long[n];
    for (int i = 0; i < n; i++) {
      array[i] = nextLong();
    }
    return array;
  }

  public double[] nextDoubleArray(int n) {
    double[] array = new double[n];
    for (int i = 0; i < n; i++) {
      array[i] = nextDouble();
    }
    return array;
  }

  public int[][] nextIntMatrix(int n, int m) {
    int[][] matrix = new int[n][m];
    for (int i = 0; i < n; i++) {
      for (int j = 0; j < m; j++) {
        matrix[i][j] = nextInt();
      }
    }
    return matrix;
  }

  public long[][] nextLongMatrix(int n, int m) {
    long[][] matrix = new long[n][m];
    for (int i = 0; i < n; i++) {
      for (int j = 0; j < m; j++) {
        matrix[i][j] = nextLong();
      }
    }
    return matrix;
  }

  public double[][] nextDoubleMatrix(int n, int m) {
    double[][] matrix = new double[n][m];
    for (int i = 0; i < n; i++) {
      for (int j = 0; j < m; j++) {
        matrix[i][j] = nextDouble();
      }
    }
    return matrix;
  }
}

