using namespace std;
template <typename T>
void in(T &x) {
  x = 0;
  bool neg = 0;
  register T c = getchar();
  if (c == '-') neg = 1, c = getchar();
  while ((c < 48) || (c > 57)) c = getchar();
  for (; c < 48 || c > 57; c = getchar())
    ;
  for (; c > 47 && c < 58; c = getchar()) x = (x << 3) + (x << 1) + (c & 15);
  if (neg) x *= -1;
}
template <typename T>
void ou(T n) {
  bool neg = 0;
  if (n < 0) n *= -1, neg = 1;
  char snum[65];
  int i = 0;
  do {
    snum[i++] = n % 10 + '0';
    n /= 10;
  } while (n);
  --i;
  if (neg) putchar('-');
  while (i >= 0) putchar(snum[i--]);
  putchar(' ');
}
vector<int> fa;
int mul(int a, int b) {
  long long res =
      ((a % (int)1000000007) * 1ll * (b % (int)1000000007)) % (int)1000000007;
  return res;
}
int mad(int a, int b) {
  long long res =
      (((a % (int)1000000007 + b % (int)1000000007) + (int)1000000007) %
       (int)1000000007);
  return res;
}
int power(int x, int y) {
  long long res = 1;
  while (y) {
    if (y & 1) res = mul(res, x);
    y >>= 1;
    x = mul(x, x);
  }
  return res;
}
int inv(int x) { return power(x, (int)1000000007 - 2); }
void fact() {
  fa.push_back(1);
  for (int i = 1; i < (int)3e5 + 5; i++) fa.push_back(mul(fa.back(), i));
}
int comb(int n, int r) {
  if (r > n) return 0;
  long long res = mul(fa[n], inv(mul(fa[n - r], fa[r])));
  return res;
}
void pv(vector<int> v, int a, int sz) {
  for (int i = a; i < (int)sz; i++) ou(v[i]);
  cout << "\n";
}
void rpv(vector<int> v, int sz, int a) {
  for (int i = sz; i >= a; --i) ou(v[i]);
  cout << "\n";
}
void pa(int z[], int a, int sz) {
  for (int i = a; i < (int)sz; i++) ou(z[i]);
  cout << "\n";
}
void rpa(int z[], int a, int sz) {
  for (int i = a; i >= sz; --i) ou(z[i]);
  cout << "\n";
}
int main() {
  int n, m, d;
  in(n), in(m), in(d);
  long long s = 0;
  vector<int> v(m), ans;
  for (int i = 0; i < (int)m; i++) in(v[i]), s += v[i];
  int res = 0, temp = 0, cnt = 0, g = 1;
  for (int i = 0; i < (int)m; i++) {
    if (n - (res + d - 1) >= s) {
      res += (d - 1);
      temp = d - 1;
      while (temp--) ans.push_back(0);
      res += v[i];
      s -= v[i];
      temp = v[i];
      while (temp--) ans.push_back(g);
      g++;
    } else {
      temp = n - res;
      cnt = 0;
      while (temp - cnt > s && cnt < d - 1) ans.push_back(0), cnt++;
      res += cnt;
      res += v[i], s -= v[i];
      temp = v[i];
      while (temp--) ans.push_back(g);
      g++;
    }
  }
  if (res + d - 1 < n)
    cout << "No"
         << "\n";
  else {
    while (res < n) ans.push_back(0), res++;
    cout << "Yes"
         << "\n";
    pv(ans, 0, n);
