			if (p < 0) { System.out.println("NO"); return; }
			for (int j = 0; j < platforms[i]; j++) { arr[p-j] = i+1; } p -= platforms[i];
		}
		// Second OP
		int playerPos = -1 + k; int platIndex = 0;
		for (; playerPos < riverLen;) {
			if (arr[playerPos] == 0 && platIndex < platforms.length) {
				Replace(platIndex, playerPos); playerPos += platforms[platIndex]-1; platIndex++;
			}
			else if (arr[playerPos] == 0) {
				System.out.println("NO"); return;
			}
			else {
				playerPos += k;
			}
		}
		System.out.println("YES");
		Print();
		//--------
	}

	static void Replace(int index, int start) {
		int platNum = index+1, platSize = platforms[index]; int s = 0;
		for (int i = 0; i < arr.length; i++) { if (arr[i] == platNum) {s = i; break;} }
		int j = 0;
		for (int i = s; j < platSize; i++) {
			arr[i] = 0;
			j++;
		}
		j = 0;
		for (int i = start; j < platSize; i++) {
			arr[i] = platNum;
			j++;
		}
	}

	static void Print() {
		for (int i = 0; i < arr.length; i++) { System.out.print(arr[i] + " "); } System.out.println();
	}
}

