import java.util.Scanner;

public class Solution {

    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int riverWidth = scanner.nextInt();
        int numPlatforms = scanner.nextInt();
        int maxJump = scanner.nextInt();

        int[] platformWidth = new int[numPlatforms];
        for (int i = 0; i < numPlatforms; ++i) {
            platformWidth[i] = scanner.nextInt();
        }

        int[] ans = new int[riverWidth + 2];

        for (int i = numPlatforms - 1, pos = riverWidth; i >= 0; --i) {
            for (int len = 0; len < platformWidth[i]; ++len) {
                ans[pos - len] = i + 1;
            }
            pos -= platformWidth[i];
        }

        int now = 0;
        while (true) {
            while (now + 1 < riverWidth + 1 && ans[now + 1] > 0) {
                ++now;
            }
            if (now + maxJump >= riverWidth + 1) {
                break;
            }
            if (ans[now + maxJump] == 0) {
                int lpos = -1;
                for (int i = now + maxJump; i < riverWidth + 2; ++i) {
                    if (ans[i] != 0) {
                        lpos = i;
                        break;
                    }
                }
                if (lpos == -1) {
                    System.out.println("NO");
                    return;
                }
                int rpos = -1;
                for (int i = lpos; i < riverWidth + 2; ++i) {
                    if (ans[i] == ans[lpos]) {
                        rpos = i;
                    }
                }
                while (ans[now + maxJump] == 0) {
                    int temp = ans[lpos - 1];
                    ans[lpos - 1] = ans[rpos];
                    ans[rpos] = temp;
                    --lpos;
                    --rpos;
                }
            }
            now += maxJump;
        }

        System.out.println("YES");
        for (int i = 1; i <= riverWidth; ++i) {
            System.out.print(ans[i] + " ");
        }
    }

}

