import java.util.*;
import java.math.*;
public class Solution {
	public static void main(String[] args) {
		Scanner in = new Scanner(System.in);
		int n = in.nextInt();
		int m = in.nextInt();
		int d = in.nextInt();
		int[] wood = new int[m];
		int[] river = new int[n + 2];
		int[] last_pos = new int[m];
		river[0] = 1;
		river[n + 1] = 1;
		for(int i = 0; i < m; i++) wood[i] = in.nextInt();
		int pos = n;
		for(int i = m - 1; i >= 0; i--) {
			last_pos[i] = pos;
			for(int j = 0; j < wood[i]; j++) {
				river[pos] = (i + 1); 
				pos -= 1;
			}
		}
		String ans = "NO";
		int next_wood = 0;
		int cur = Math.min(n + 1, d);
		while(cur < n + 2) {
			if(river[cur] != 0) {
				ans = "YES";
				break;
			}else {
				if(next_wood < m) {
					for(int i = last_pos[next_wood]; i > (last_pos[next_wood] - wood[next_wood]); i--) {
						river[i] = 0;
					}
					int temp_cur = cur;
					for(int i = cur; i < (cur + wood[next_wood]); i++) {
						river[i] = (next_wood + 1);
						temp_cur = i;
					}
					cur = Math.min(n + 1, temp_cur + d);
					next_wood += 1;
				}else {
					break;
				}
			}
		}
		if(ans.equals("YES")) {
			System.out.println("YES");
			for(int i = 1; i < n + 1; i++) {
				if(i > 1) System.out.print(" ");
				System.out.print(river[i]);
			}
			System.out.print("\n");
		}else {
			System.out.println("NO");
		}
	}
}

