	static class Node {
		int len;
		int ind;
		int left;

		Node(int len, int ind) {
			this.len=len;
			this.ind=ind;
			this.left=0;
		}

		public String toString() {
			return this.len + " " + this.ind + " " + this.left;
		}
	}

