public class Riverjump
{
	public static void main(String args[])throws IOException
	{
		BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
		StringTokenizer st = new StringTokenizer(br.readLine());
		int n = Integer.parseInt(st.nextToken());
		int m = Integer.parseInt(st.nextToken());
		int d = Integer.parseInt(st.nextToken());
		int tile[] = new int[m];
		int sum=0;
		st = new StringTokenizer(br.readLine());
		for(int i=0;i<m;i++){
			tile[i] = Integer.parseInt(st.nextToken());
			sum += tile[i];
		}
		int jumps=(n-sum)/d;
		int ljump=0;
		if(n-sum>d)
			ljump = (n-sum)%d;
		int tnum=1;
		boolean flag=false;
		int river[]= new int[n+1];
		for(int i = jumps>0?d:1;i<=n;i++)
		{
			if(tile[tnum-1]>0){
			river[i]=tnum;
			tile[tnum-1]--;
		}
		else
		{
			jumps--;
			if(jumps>0)
				i=i+d-2;
			else if(ljump>0)
			{

				i=i+ljump-1;
				ljump=0;
			}
			else
				i--;
			tnum++;
			if(tnum>tile.length)
				break;
		}

		}
		//boolean flag=false;
		if(d>n)
			flag=true;
		else{
		for(int i=n;i>=n-d+1;i--)
		{
			if(river[i]!=0){
				flag=true;
				break;
			}
		}
	}
	if(flag==true){
		System.out.println("YES");
		for(int i=1;i<=n;i++)
			System.out.print(river[i]+" ");
		System.out.println();
		//System.out.println(sum+" "+(n-sum)/d);
	}
	else
		System.out.println("NO");
	}
}

