  for (int i = 1; i <= m; i++) {
    int here = pos[i] - prev - 1;
    while (here--) printf("0 ");
    here = c[i];
    while (here--) printf("%d ", i);
    prev = pos[i] + c[i] - 1;
  }
  int here = n - prev;
  while (here--) printf("0 ");
  return 0;
}

