    n, m, d = map(int, input().split())
    C = list(map(int, input().split()))
    if (d - 1) * (m + 1) < n - sum(C):
        print("NO")
        return

    print("YES")
    D = [d - 1]*(m + 1)
    rep = (d - 1)*(m + 1) - (n - sum(C))
    for i in range(rep):
