public class Main {
    BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
    StringTokenizer st = new StringTokenizer("");
    PrintWriter pw = new PrintWriter(System.out);

    int nextInt() throws IOException {
        return Integer.parseInt(next());
    }

    String next() throws IOException {
        if (!st.hasMoreTokens()) {
            st = new StringTokenizer(br.readLine());
        }
        return st.nextToken();
    }

    long nextLong() throws IOException {
        return Long.parseLong(next());
    }

    double nextDouble() throws IOException {
        return Double.parseDouble(next());
    }

    public Main() throws FileNotFoundException {
    }

    public static void main(String[] args) throws IOException {
        new Main().run();
    }

    long mod = (long) 1e9 + 7;

    void run() throws IOException {
        int n = nextInt();
        int m = nextInt();
        int d = nextInt();
        Pair[] a = new Pair[m];
        int sum = 0;
        for (int i = 0; i < a.length; i++) {
            a[i] = new Pair(nextInt(), i);
            sum += a[i].x;
        }
        int[] ans = new int[n];
        Arrays.fill(ans, -1);
        int pos = ans.length - 1;
        for (int i = a.length - 1; i >= 0; i--) {
            for (int j = pos; j > pos - a[i].x; j--) {
                ans[j] = a[i].y;
            }
            pos -= a[i].x;
        }
        int start = n - sum;
        int next = 0;
        while (true) {
            if (start >= n || next + d >= n || ans[next + d - 1] != -1) break;
            int e = ans[start];
            int x = start;
            while (x < n && ans[x] == e) {
                ans[x] = -1;
                x++;
            }
            int cur = x - start;
            start = x;
            for (int i = 0; i < cur; i++) {
                ans[next + i + d - 1] = e;
            }
            next += d + cur - 1;
        }
        int last = 0;
        for (int i = 0; i < ans.length; i++) {
            if (ans[i] != -1) last = i;
        }
        if(last + d < n){
            pw.print("NO");
            pw.close();
            return;
        }
        pw.println("YES");
        for (int i = 0; i < ans.length; i++) {
            pw.print((ans[i] + 1) + " ");
        }
        pw.close();
    }

    class Pair {
        int x, y;

        public Pair(int a, int b) {
            x = a;
            y = b;
        }
    }

    class PairComp implements Comparator<Pair> {

        @Override
        public int compare(Pair o1, Pair o2) {
            if (o1.x == o2.x) return Integer.compare(o1.y, o2.y);
            return Integer.compare(o1.x, o2.x);
        }
    }
}

import java.io.OutputStream;
import java.io.IOException;
import java.io.InputStream;
import java.io.PrintWriter;
import java.util.Scanner;

/**
 * Built using CHelper plug-in
 * Actual solution is at the top
 */
public class Main
{
    public static void main(String[] args)
    {
        InputStream inputStream = System.in;
        OutputStream outputStream = System.out;
        Scanner in = new Scanner(inputStream);
        PrintWriter out = new PrintWriter(outputStream);
        TaskC solver = new TaskC();
        solver.solve(1, in, out);
        out.close();
    }

    static class TaskC
    {
        public void solve(int testNumber, Scanner in, PrintWriter out)
        {
            int n = in.nextInt();
            int m = in.nextInt();
            int d = in.nextInt();
            in.nextLine();
            String platformsString = in.nextLine();
            String[] platforms = platformsString.split(" ");
            int platformsNums[] = convert(platforms);

            int river[] = new int[n + 2];

            for (int i = m - 1, pos = n; i >= 0; --i)
            {
                for (int len = 0; len < platformsNums[i]; ++len)
                {
                    river[pos - len] = i + 1;
                }
                pos -= platformsNums[i];
            }

            int currentPos = 0;
            while (true)
            {
                while (currentPos + 1 < n + 1 && river[currentPos + 1] > 0) ++currentPos;

                if (currentPos + d >= n + 1) break;
                //needs a platform?
                if (river[currentPos + d] == 0)
                {
                    int lpos = -1;
                    //identify first platform
                    for (int i = currentPos + d; i < n + 2; ++i)
                    {
                        if (river[i] != 0)
                        {
                            lpos = i;
                            break;
                        }
                    }
                    // no more platforms
                    if (lpos == -1)
                    {
                        out.print("NO");
                        return;
                    }
                    //detect end of the current platform
                    int rpos = -1;
                    for (int i = lpos; i < n + 2; ++i)
                    {
                        if (river[i] == river[lpos])
                            rpos = i;
                        else
                            break;
                    }
                    // keep shifting till getting the platform
                    while (river[currentPos + d] == 0)
                    {
                        //swap
                        int t = river[lpos - 1];
                        river[lpos - 1] = river[rpos];
                        river[rpos] = t;
                        --lpos;
                        --rpos;
                    }
                }
                currentPos += d;
            }
            out.println("YES");
            for (int i = 1; i <= n; i++)
            {
                out.print(river[i] + " ");
            }
        }

        public static int[] convert(String[] platforms)
        {
            int nums[] = new int[platforms.length];
            for (int i = 0; i < platforms.length; i++)
            {
                nums[i] = Integer.parseInt(platforms[i]);
            }
            return nums;
        }

    }
}

