int n, m, d;
deque<pair<int, int>> deq;
int check() {
  int pos = deq.back().first + deq.back().second - 1;
  if ((pos + d) >= n + 1)
    return 1;
  else
    return 0;
}
void solve() {
  deq.clear();
  deq.push_back({0, 1});
  cin >> n >> m >> d;
  for (int i = 0; i < m; ++i) {
    int x;
    cin >> x;
    deq.push_back({deq.back().first + deq.back().second, x});
  }
  int lst = deq.size() - 1;
  while (true) {
    int x = check();
    if (x == 0) {
      if (lst == 0) {
        cout << "NO";
        return;
      }
      for (int i = lst; i < deq.size(); ++i) {
        deq[i].first = deq[i - 1].first + deq[i - 1].second - 1 + d;
      }
      --lst;
      continue;
    }
    cout << "YES\n";
    vector<int> ans(n + 1);
    for (int i = 1; i < deq.size(); ++i) {
      for (int j = 0; j < deq[i].second; ++j) {
        ans[deq[i].first + j] = i;
      }
    }
    for (int i = 1; i < ans.size(); ++i) cout << ans[i] << " ";
    cout << endl;
    return;
  }
}
int main() {
  ios::sync_with_stdio(false);
  cin.tie(nullptr);
  solve();
  return 0;
}

