public class CF598C2 {

	private static int n, m, d;
	private static int[] c;

	public static void main(String[] args) throws IOException {
		BufferedReader br = new BufferedReader(new InputStreamReader(System.in));

		String[] inputs = br.readLine().split(" ");
		n = Integer.parseInt(inputs[0]);
		m = Integer.parseInt(inputs[1]);
		d = Integer.parseInt(inputs[2]);

		int platformLength = 0;
		c = new int[m];
		inputs = br.readLine().split(" ");
		for (int ii = 0; ii < m; ii++) {
			c[ii] = Integer.parseInt(inputs[ii]);
			platformLength += c[ii];
		}

		solve(platformLength);
	}

	private static void solve(int platformLength) {
		if (platformLength + ((d-1) * (m+1)) < n) {
			System.out.println("NO");
			return;
		}

		int intervalLength = n - platformLength;
		int dd = intervalLength / (m+1);
		int ddRem = intervalLength % (m+1);

		System.out.println("YES");
		int label = 0;
		for (int ii = 0; ii < m; ii++) {
			int bonus = 0;
			if (ddRem > 0) {
				bonus = 1;
				ddRem--;
			}
			for (int jj = 0; jj < dd+bonus; jj++) {
				System.out.print("0 ");
			}
			label++;
			for (int jj = 0; jj < c[ii]; jj++) {
				System.out.print(label + " ");
			}
		}
		for (int jj = 0; jj < (dd+ddRem); jj++) {
			System.out.printf("0%c", (jj == dd+ddRem-1) ? '\n' : ' ');
		}
	}
}

