def sieve(n):
    prime = [True for i in range(n + 1)]
    p = 2
    while (p * p <= n):
        if (prime[p] == True):
            for i in range(p * 2, n + 1, p):
                prime[i] = False
        p += 1
    prime[0] = prime[1] = False
    r = [p for p in range(n + 1) if prime[p]]
    return r
def pow(x, y, mod):
    r = 1
    x = x % mod
    while y > 0:
        if y & 1:
            r = (r * x) % mod
        y = y >> 1
        x = (x * x) % mod
    return r
