int ans[N];
int main() {
  ios_base::sync_with_stdio(0);
  cin.tie(0);
  int n, m, d;
  cin >> n >> m >> d;
  for (int i = 1; i <= m; i++) {
    cin >> c[i];
  }
  for (int i = m, pos = n; i >= 1; i--) {
    for (int j = 0; j < c[i]; j++) {
      ans[pos - j] = i;
    }
    pos -= c[i];
  }
  int cur = 0;
  while (1) {
    while (cur + 1 < n + 1 && ans[cur + 1] > 0) cur++;
    if (cur + d >= n + 1) break;
    if (ans[cur + d] == 0) {
      int l = -1;
      for (int i = cur + d; i <= n + 1; i++) {
        if (ans[i] != 0) {
          l = i;
          break;
        }
      }
      if (l == -1) {
        cout << "NO\n";
        return 0;
      }
      int r = -1;
      for (int i = l; i <= n + 1; i++) {
        if (ans[i] == ans[l]) r = i;
      }
      while (ans[cur + d] == 0) {
        swap(ans[l - 1], ans[r]);
        l--;
        r--;
      }
    }
    cur += d;
  }
  cout << "YES\n";
  for (int i = 1; i <= n; i++) {
    cout << ans[i] << " ";
  }
  return 0;
}

