	static class Task {
		public void solve(int testNumber, InputReader in, PrintWriter out) {
			int n = in.nextInt();
			int m = in.nextInt();
			int d = in.nextInt();
			int sum = 0;
			ArrayList<Integer> ar = new ArrayList<Integer>();
