			}
			else {
				out.println("YES");
				int jump = (n - sum)/(m+1);
				int rem = (n - sum) % (m+1);
				int ctr = 0;
				printgap(!(rem == 0 || ctr >= rem), jump , out);
				if(!(rem == 0 || ctr >= rem)) ctr++;
				for(int i = 0; i < ar.size(); i++) {
