        InputStream inputStream = System.in;
        OutputStream outputStream = System.out;
        InputReader in = new InputReader(inputStream);
        PrintWriter out = new PrintWriter(outputStream);
        TaskC solver = new TaskC();
        solver.solve(1, in, out);
        out.close();
    }

    static class TaskC {
        public void solve(int testNumber, InputReader in, PrintWriter out) {
            int n = in.nextInt();
            int m = in.nextInt();
            int d = in.nextInt();
            int[] c = new int[m + 1];
            for (int i = 0; i < m; i++) {
                c[i + 1] = in.nextInt();
            }
            int[] ans = new int[n + 1];
            boolean no = false;
            int curPlatform = 1;
            int last = 0;
            for (int i = 1; i <= n; ) {
                if (i - last > d || (i + d <= n && curPlatform > m)) {
                    no = true;
                    break;
                }
                if (curPlatform > m) break;
                int idx = i + c[curPlatform] - 1;
                int idxPlatform = curPlatform + 1;
                boolean ok = true;
                while (idx <= n) {
                    idx += d;
                    if (idx <= n) {
                        if (idxPlatform > m) {
                            ok = false;
                            break;
                        }
                        idx += c[idxPlatform] - 1;
                        idxPlatform++;
                    }
                }
                if (ok == true) {
                    for (int j = i; j <= i + c[curPlatform] - 1; j++) ans[j] = curPlatform;
                    last = i + c[curPlatform] - 1;
                    i += c[curPlatform];
                    curPlatform++;
                } else
                    i++;
            }
            if (no) out.println("NO");
            else {
                out.println("YES");
                for (int i = 1; i <= n; i++) {
                    if (i > 1) out.print(" ");
                    out.print(ans[i]);
                }
                out.println();
            }
        }

    }

    static class InputReader {
        private byte[] buf = new byte[1024];
        private int curChar;
        private int numChars;
        private InputStream stream;

        public InputReader(InputStream stream) {
            this.stream = stream;
        }

        private boolean isWhitespace(int c) {
            return c == ' ' || c == '\n' || c == '\r' || c == '\t' || c == -1;
        }

        private int read() {
            if (numChars == -1)
                throw new InputMismatchException();
            if (curChar >= numChars) {
                curChar = 0;
                try {
                    numChars = stream.read(buf);
                } catch (IOException e) {
                    throw new InputMismatchException();
                }
                if (numChars <= 0)
                    return -1;
            }
            return buf[curChar++];
        }

        public int nextInt() {
            int c = read();
            while (isWhitespace(c))
                c = read();
            int sgn = 1;
            if (c == '-') {
                sgn = -1;
                c = read();
            }
            int res = 0;
            do {
                if (c < '0' || c > '9')
                    throw new InputMismatchException();
                res *= 10;
                res += c - '0';
                c = read();
            } while (!isWhitespace(c));
            return res * sgn;
        }

    }
}

