		int[] end = new int[m];

		int prev = 0;
		for(int i = 0; i < m; i++) {
			start[i] = prev + 1;
			prev = end[i] = prev + len[i];
		}

		prev = n + 1;
		for(int i = m - 1; i >= 0; i--) {
			int diff = prev - end[i];
			if(diff <= d)
				break;
			diff -= d;
			end[i] += diff;
			prev = start[i] += diff;
		}

		if(start[0] <= d) {
			pw.println("YES");
			int[] ans = new int[n];
			for(int i = 0; i < m; i++)
				for(int j = start[i] - 1; j < end[i]; j++)
					ans[j] = i + 1;
			for(int i = 0; i < n; i++)
				pw.print(ans[i] + (i == n - 1 ? "\n" : " "));
