vector<int> res;
bool canJump(int n, int d, vector<int> &len) {
  int m = len.size();
  res.clear();
  int totLen = 0;
  for (int i = 0; i < m; i++) {
    totLen += len[i];
  }
  if (totLen + (m + 1) * (d - 1) < n) return false;
  int req = n - totLen;
  int cont = 0;
  bool isGap = true;
  if (req == 0) isGap = false;
  for (int i = 0; i < m; i++) {
    while (isGap) {
      cont++;
      req--;
      if (cont == d - 1 || req == 0) {
        cont = 0;
        isGap = false;
      }
      res.push_back(0);
    }
    for (int j = 0; j < len[i]; j++) {
      res.push_back(i + 1);
    }
    if (req != 0) isGap = true;
  }
  if (req != 0) {
    while (req != 0) {
      res.push_back(0);
      req--;
    }
  }
  return true;
}
