  int n, m, d;
  cin >> n >> m >> d;
  vector<int> len(m);
  for (int i = 0; i < m; i++) cin >> len[i];
  if (canJump(n, d, len)) {
    cout << "YES" << endl;
    printVec(res);
  } else {
    cout << "NO" << endl;
  }
}

