    static class CPlatformsJumping {
        PrintWriter out;
        InputReader in;

        public void solve(int testNumber, InputReader in, PrintWriter out) {
            this.out = out;
            this.in = in;
            int n = ni();
            int m = ni();
            int d = ni();
            int[] arr = new int[m];
            int sum = 0;
            int i = 0;
            for (i = 0; i < m; i++)
                sum += arr[i] = ni();
            if (n > sum + (d - 1) * (m + 1)) {
                pn("NO");
                return;
            }
            int[] stats = new int[n + 1];
            int pos = 0;
            int[] sf = new int[m];
            sf[m - 1] = arr[m - 1];
            for (i = m - 2; i >= 0; i--)
                sf[i] += sf[i + 1] + arr[i];
            d = Math.min(d, n);
            for (i = 0; i < m; i++) {
                if (pos + d - 1 + sf[i] <= n) {
                    pos += d;
                    for (int j = pos; j < pos + arr[i]; j++)
                        stats[j] = i + 1;
                    pos += arr[i] - 1;
                } else {
                    int allowed = n + 1 - sf[i] - pos;
                    pos += allowed;
                    for (int j = pos; j < pos + arr[i]; j++)
                        stats[j] = i + 1;
                    pos += arr[i];
                }
            }
            pn("YES");
            for (i = 1; i <= n; i++)
                p(stats[i] + " ");
            pn("");
        }

        int ni() {
            return in.nextInt();
        }

        void pn(String zx) {
            out.println(zx);
        }

        void p(Object o) {
            out.print(o);
        }

    }

    static class InputReader {
        private InputStream stream;
        private byte[] buf = new byte[1024];
        private int curChar;
        private int numChars;

        public InputReader(InputStream stream) {
            this.stream = stream;
        }

        public int read() {
            if (numChars == -1) {
                throw new UnknownError();
            }
            if (curChar >= numChars) {
                curChar = 0;
                try {
                    numChars = stream.read(buf);
                } catch (IOException e) {
                    throw new UnknownError();
                }
                if (numChars <= 0) {
                    return -1;
                }
            }
            return buf[curChar++];
        }

        public int nextInt() {
            return Integer.parseInt(next());
        }

        public String next() {
            int c = read();
            while (isSpaceChar(c)) {
                c = read();
            }
            StringBuffer res = new StringBuffer();
            do {
                res.appendCodePoint(c);
                c = read();
            } while (!isSpaceChar(c));

            return res.toString();
        }

        private boolean isSpaceChar(int c) {
            return c == ' ' || c == '\n' || c == '\r' || c == '\t' || c == -1;
        }

    }
}

