public class Solution {

	static void printArray(int[] a) {
		for (int i = 0; i < a.length; i++) {
			System.out.print(String.valueOf(a[i]) + " ");
		}
	}

	static int[] solve(int n, int m, int d, int[] c) {
		int cSum = 0;
		for (int i = 0; i < m; i++) {
			cSum += c[i];
		}
		if ((m + 1) * (d - 1) + cSum < n) {
			return null;
		} else {
			int water = n - cSum;
			int[] ret = new int[n];
			int cIndex = 0;
			int continueWater = 0;
			for (int i = 0; i < n; i++) {
				if (continueWater < d - 1 && water > 0) {
					ret[i] = 0;
					continueWater++;
					water--;
				} else {
					for (int j = 1; j <= c[cIndex]; j++, i++) {
						ret[i] = cIndex + 1;
					}
					i--;
					cIndex++;
					continueWater = 0;
				}
			}
			return ret;
		}
	}

    private static final Scanner scanner = new Scanner(System.in);

    public static void main(String[] args) throws IOException {
    	String[] strs = scanner.nextLine().split(" ");
    	int n = Integer.valueOf(strs[0]);
    	int m = Integer.valueOf(strs[1]);
    	int d = Integer.valueOf(strs[2]);
    	int[] c = new int[m];
    	strs = scanner.nextLine().split(" ");
    	for (int i = 0; i < m; i++) {
    		c[i] = Integer.valueOf(strs[i]);
    	}
    	int[] ret = solve(n, m, d, c);
    	if (ret != null) {
    		System.out.println("YES");
    		printArray(ret);
    	} else {
    		System.out.println("NO");
    	}
    	scanner.close();
    }
}

