		sum+=arr[i];
	}
	//System.out.println(sum);
	boolean can=false;
	if(d!=1&&d*(m+1)+sum-m>n) {
		System.out.println("YES");
		can =true;
	}
	else {
		if(d==1 && sum==n) {
			System.out.println("YES");
			can =true;
		}
		else {
		System.out.println("NO");
	}
	}
	int res []=new int [n];
	ArrayList<Integer> ind=new ArrayList<Integer>();
	if(can) {

		int ob=0;
		int oe=0;
		int nb=0;
		int ne=0;
		int wlo=n;
		int wolo=sum;
		int wln=n;
		int woln=sum;

		boolean fit=true;
		for(int i=0;i<m;i++) {
			//System.out.println(fit);
			fit=true;
			nb=ob+d;
			ne=nb+arr[i]-1;
			wln= wlo-d-arr[i]+1;
			woln=wolo-arr[i];
			if(wln>=woln) {
				wlo=wln;
				wolo=woln;
			//	System.out.println("v"+nb);
				ind.add(nb);
				ob=ne;
				oe=ne;
			}
			else {
				wln=wlo;
				woln=wolo;
				nb=ob;
				ne=oe;
				//i--;
				fit=false;
			}
			if(!fit) {
				nb=ob+1;
				ne=nb+arr[i]-1;
				ind.add(nb);
				ob=ne;
				oe=ne;
				//System.out.println("iv"+ob);

				wlo=wlo-arr[i];
				wolo=wolo-arr[i];
				wln=wlo;
				woln=wolo;

			}

		}

	int k=1;
	for(int x:ind) {
		//System.out.println(x+" "+arr[k-1]);
