    void solve() {
        int n = in.nextInt(), m = in.nextInt(), d = in.nextInt();
        int[] len = new int[m+1];
        for(int i = 0; i < m; i++){
            len[i] = in.nextInt();
        }

        int[] idx = new int[m+1];
        idx[m] = n+1;
        for(int i = m-1; i >= 0; i--){
            idx[i] = idx[i+1] - len[i];
        }

        int now = 0;
        for(int i = 0; i < m; i++){
            int next = now + d;
            if(next >= n + 1) break;
            idx[i] = Math.min(idx[i], next);
            now = idx[i] + len[i] - 1;
        }

