import java.io.*;
import java.util.Collections;
import java.util.StringTokenizer;

public class Solution {

    FastScanner in;
    PrintWriter out;

    public void solution() throws IOException {
        int n = in.nextInt();
        int m = in.nextInt();
        int d = in.nextInt();
        int[] c = new int[m];
        int cSum = 0;
        for(int i = 0; i < m; ++i) {
            c[i] = in.nextInt();
            cSum += c[i];
        }
        int maxNumberOfJumps = m + 1;
        if (maxNumberOfJumps * (d - 1) + cSum < n) {
            out.print("NO");
            return;
        }

        int space = n - cSum;
        int[] pos= new int[m];
        int curPos = 1;
        for(int i = 0; i < m; ++i) {
            int  jump = Math.min(space, d-1);
            space -= jump;
            curPos += jump;
            pos[i] = curPos;
            curPos += c[i];
        }
        out.println("YES");
        int[] res = new int[n];
        for(int i = 0; i < n; ++i){
            res[i] = 0;
        }
        for(int i = 0; i < m; ++i){
            for(int j = pos[i] - 1; j < pos[i] + c[i] - 1; ++j){
                res[j] = i + 1;
            }
        }
        for(int i = 0; i < n; ++i){
            out.print(res[i] + " ");
        }
    }

    public void run() {
        try {
            in = new FastScanner(System.in);
            out = new PrintWriter(System.out);

            solution();

            in.close();
            out.close();
        } catch (IOException e) {
            e.printStackTrace();
        }

    }

    class FastScanner {
        BufferedReader br;
        StringTokenizer st;

        FastScanner(InputStream is){
            br = new BufferedReader(new InputStreamReader(is));
        }

        String next() {
            while (st == null || !st.hasMoreTokens()) {
                try {
                    st = new StringTokenizer(br.readLine());
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
            return st.nextToken();
        }

        int nextInt() {
            return Integer.parseInt(next());
        }

        void close() {
            try {
                br.close();
            } catch (IOException e) {
                e.printStackTrace();
            }

        }
    }

    public static void main(String[] args) {
        new Solution().run();
    }
}

