"""*********************** GENERATE ALL PRIME NUMBERS SMALLER THAN OR EQUAL TO n ***************************"""


def SieveOfEratosthenes(n):

    prime = [True for i in range(n + 1)]
    p = 2
    while (p * p <= n):

        if (prime[p] == True):
            for i in range(p * 2, n + 1, p):
                prime[i] = False

        p += 1
    prime[0] = False
    prime[1] = False

    lst = []
    for p in range(n + 1):
        if prime[p]:
            lst.append(p)

    return lst


"""*************************************** FIND nCr ********************************************************"""


def nCr(n, r):
    a = 1
    b = 1
    c = 1
    for i in range(1, n + 1):
        c *= i
    for i in range(1, r + 1):
        b *= i
    for i in range(1, n - r + 1):
        a *= i

    return (c // (a * b))


"""************************* GET PRIME FACTORS AND THEIR POWERS FOR AN INTEGER *****************************"""


def sieveOfEratosthenes1(N, s):

    prime = [False] * (N+1)

    for i in range(2, N+1, 2):
        s[i] = 2

    for i in range(3, N+1, 2):
        if (prime[i] == False):

            s[i] = i

            for j in range(i, int(N / i) + 1, 2):
                if (prime[i*j] == False):
                    prime[i*j] = True

                    s[i * j] = i


def generatePrimeFactors(N):

    s = [0] * (N+1)

    sieveOfEratosthenes1(N, s)

    # print("Factor Power")

    curr = s[N]

    cnt = 1

    factors = []
    power = []
    while (N > 1):
        N //= s[N]

        if (curr == s[N]):
            cnt += 1
            continue

        # curr is factor and cnt in the power of this factor
        factors.append(curr)
        power.append(cnt)

        curr = s[N]
        cnt = 1

    return factors, power


