        int d=ip.nextInt();
        int arr[]=new int[m];
        int pd=0;
        for(int i=0;i<m;i++){
            arr[i]=ip.nextInt();
            pd+=arr[i];
        }
        int path[]=new int[n+1];
        int type=m;
        int j=n;
        for(int i=m-1;i>=0;i--){
            int length=arr[i];
            int c=0;
            while(c!=length){
                path[j]=type;
                c++;
                j--;
            }
            type--;
        }
        Queue<Integer> pq=new LinkedList<>();
        int v=0;
        for(int i=1;i<=n;i++){
            if(path[i]>v){
                pq.add(i);
                v=path[i];
            }
