int N, M, D;
int C[MAX_N];
int main() {
  ios_base::sync_with_stdio(false);
  cin.tie(NULL);
  cout.tie(NULL);
  cin >> N >> M >> D;
  int segments = 0;
  for (int i = 1; i <= M; i++) {
    cin >> C[i];
    segments += C[i];
  }
  int blank = max(0, N - segments);
  deque<int> river;
  int left = 0;
  for (int i = 1; i <= M; i++) {
    int next = left + min(D, blank);
    for (int j = left + 1; j < next; j++) {
      river.push_back(0);
      if (blank > 0) blank--;
    }
    for (int j = next; j <= next + C[i] - 1; j++) river.push_back(i);
    left = next + C[i] - 1;
  }
  for (int i = left + 1; i < left + D; i++) river.push_back(0);
  if (river.size() < N) {
    cout << "NO";
    return 0;
  }
  while (river.size() > N && river[0] == 0) {
    river.pop_front();
  }
  while (river.size() > N && river.back() == 0) {
    river.pop_back();
  }
  if (river.size() > N) {
    vector<int> ans(river.begin(), river.end());
    for (int i = 0; i < ans.size() && ans.size() > N; i++) {
      if (ans[i] == 0) {
        ans.erase(ans.begin() + i);
        i -= 1;
      }
    }
    river = deque<int>(ans.begin(), ans.end());
  }
  cout << "YES\n";
  for (int i = 0; i < N; i++) cout << river[i] << " ";
  return 0;
}

