        int a = 0;
        int k = 1;
        int b = sc.read();
        while (b < '0' || b > '9') {
            if (b == '-') k = -1;
            b = sc.read();
        }
        while (b >= '0' && b <= '9') {
            a = a * 10 + (b - '0');
            b = sc.read();
        }
        return a * k;
    }

