public class PlatformsJumping {

	// https://codeforces.com/contest/1256/problem/C

	public static void main(String[] args) throws IOException, FileNotFoundException {
		BufferedReader in = new BufferedReader(new InputStreamReader(System.in));
		//BufferedReader in = new BufferedReader(new FileReader("PlatformsJumping"));

		StringTokenizer st = new StringTokenizer(in.readLine());
		int n = Integer.parseInt(st.nextToken());
		int m = Integer.parseInt(st.nextToken());
		int d = Integer.parseInt(st.nextToken());
		int[] arr = new int[m];
		int total=0;
		st = new StringTokenizer(in.readLine());
		for (int i=0; i<m; i++) {
			arr[i] = Integer.parseInt(st.nextToken());
			total += arr[i];
		}
		int leftover = n - total;

		int[] f = new int[n+2];
		int pointer=0;
		for (int i=0; i<n; i++) {
			//f[i+d] = pointer+1;
			if (pointer >= arr.length) break;
			if (leftover - (d-1) >= 0) {
				for (int j=i+d; j<i+d+arr[pointer] && j < n+1; j++) {
					f[j] = pointer+1;
				}
				i = i+d+arr[pointer]-2;
				leftover -= (d-1);
				pointer++;
			}
			else {
				for (int j=i+leftover+1; j<i+leftover+1+arr[pointer] && j < n+1; j++) {
					f[j] = pointer+1;
				}
				i = i+leftover+1+arr[pointer]-2;
				leftover =0;
				pointer++;
			}
		}

		int steps =0;
		for (int i=n; i>=1; i--) {
			if (f[i] != 0) break;
			steps++;
			leftover--;
		}

		if (leftover > 0 || steps+1 > d) {
			System.out.println("NO");
		}
		else {
			System.out.println("YES");
			for (int i=1; i<n+1; i++) {
				System.out.print(f[i] + " ");
			}
		}
	}
}

