      ans[pos - len] = i + 1;
    }
    pos -= c[i];
  }
  long long now = 0;
  while (true) {
    while (now + 1 < n + 1 && ans[now + 1] != 0) {
      now++;
    }
    if (now + d >= n + 1) break;
    if (ans[now + d] == 0) {
      long long lpos = -1;
      for (long long i = now + d; i < n + 2; i++) {
        if (ans[i] != 0) {
          lpos = i;
          break;
        }
      }
      if (lpos == -1) {
