      }
      long long rpos = -1;
      for (long long i = lpos; i < n + 2; i++)
        if (ans[i] == ans[lpos]) {
          rpos = i;
        }
      while (ans[now + d] == 0) {
        swap(ans[lpos - 1], ans[rpos]);
        lpos--;
        rpos--;
      }
    }
