import java.io.*;
import java.util.*;

public class Main {
    static PrintWriter out;

    public static void main(String[] args) throws IOException {
        br = new BufferedReader(new InputStreamReader(System.in));
        out = new PrintWriter(System.out);
        int n = nextInt();
        int m = nextInt();
        int d = nextInt();
        int[] a = new int[m];
        int sum = 0;
        for (int i = 0; i < m; i++) {
            a[i] = nextInt();
            sum += a[i];
        }
        int[] ans = new int[m];
        int i = 0;
        int pos = 0;
        while (pos < n + 1) {
            if (i == m) {
                if (pos + d < n + 1) {
                    out.print("NO");
                    out.close();
                    return;
                } else {
                    break;
                }
            }
            if (pos + d >= n + 1) {
                pos++;
                for (int j = i; j < m; j++) {
                    ans[j] = pos;
                    pos += a[j];
                }
                break;
            }
            pos =  Math.min(pos + d, n - sum + 1);
            sum -= a[i];
            ans[i] = pos;
            pos += a[i] - 1;
            i++;
        }
        out.println("YES");
        int p = 0;
        for (int j = 1; j <= n; j++) {
            if (p < m && ans[p] <= j && ans[p] + a[p] - 1 >= j) {
                out.print(p + 1 + " ");
                if (j == a[p] + ans[p] - 1)
                    p++;
            } else {
                out.print("0 ");
            }
        }
        out.close();
    }

    static BufferedReader br;
    static StringTokenizer st = new StringTokenizer("");

    static long nextLong() throws IOException {
        return Long.parseLong(next());
    }

    static int nextInt() throws IOException {
        return Integer.parseInt(next());
    }

    static String next() throws IOException {
        while (!st.hasMoreTokens()) {
            st = new StringTokenizer(br.readLine());
        }
        return st.nextToken();
    }
}

import java.io.*;
import java.util.*;

public class Q3 {

    public static void main(String[] args) {

        InputReader in = new InputReader();
        PrintWriter out = new PrintWriter(System.out);

        int n =in.nextInt(),m=in.nextInt(),d=in.nextInt(),sum=0;
        int arr[]=new int[m];
        for(int  i=0;i<arr.length;i++){
            arr[i]=in.nextInt();
            sum+=arr[i];
        }
        StringBuilder sb=new StringBuilder();
        int wait=n-sum,val=1,j=0;
        for(int i =0;i<n;){
            int cnt=0;
            for(;i<n && wait>0 && cnt<d-1;){
                cnt++;
                sb.append(0+" ");
                i++;
                wait--;
            }
            if(i==n)
                break;
            if(j==arr.length){
                out.println("NO");
                out.close();
                return;
            }
            for(;arr[j]-->0 && i<n;){
                i++;
                sb.append(val+" ");
            }
            val++;j++;
        }
        out.println("YES");
        out.println(sb);
        out.close();
    }

    static class InputReader {
        public BufferedReader reader;
        public StringTokenizer tokenizer;

        static long gcd(long a, long b) {
            return b == 0 ? a : gcd(b, a % b);
        }

        static int[][] packU(int n, int[] from, int[] to) {
            int[][] g = new int[n][];
            int[] p = new int[n];
            for (int f : from)
                p[f]++;
            for (int t : to)
                p[t]++;
            for (int i = 0; i < n; i++)
                g[i] = new int[p[i]];
            for (int i = 0; i < from.length; i++) {
                g[from[i]][--p[from[i]]] = to[i];
                g[to[i]][--p[to[i]]] = from[i];
            }
            return g;
        }

        public int[] shuffle(int[] arr) {
            Random r = new Random();
            for (int i = 1, j; i < arr.length; i++) {
                j = r.nextInt(i);
                arr[i] = arr[i] ^ arr[j];
                arr[j] = arr[i] ^ arr[j];
                arr[i] = arr[i] ^ arr[j];
            }
            return arr;
        }

        public InputReader() {
            reader = new BufferedReader(new InputStreamReader(System.in), 32768);
            tokenizer = null;
