int a[1005];
int s[1005];
int main() {
  int n, m, d, i, j, idx, cnt = 0, cur;
  cin >> n >> m >> d;
  for (i = 1; i <= m; i++) {
    cin >> a[i];
  }
  cur = 1;
  for (i = 1; i <= n; i++) {
    if (a[cur] == 0) {
      cur++;
      if (cur > m) break;
      s[i] = cur;
      a[cur]--;
    } else {
      s[i] = cur;
      a[cur]--;
    }
  }
  bool found = false;
  for (i = 1; i <= m; i++) {
    for (j = 1; j <= n; j++) {
      if (s[j] == i) break;
    }
    cnt = d - 1;
    idx = j;
    while (cnt > 0) {
      if (s[n] > 0) {
        found = true;
        break;
      }
      for (j = n - 1; j >= idx; j--) {
        swap(s[j], s[j + 1]);
      }
      idx++;
      cnt--;
    }
    if (found) break;
  }
  found = false;
  for (j = n; j >= 1; j--) {
    if (s[j] == m && j + d > n) {
      found = true;
      break;
    }
  }
  if (found) {
    cout << "YES\n";
    for (i = 1; i <= n; i++) {
      cout << s[i] << " ";
    }
