import java.io.*;
import java.util.*;

public class Problem_C {
	public static void main(String[] args) {
		InputReader in = new InputReader();
		StringBuilder out = new StringBuilder();

		int N = in.nextInt();
		int M = in.nextInt();
		int D = in.nextInt();
		int[] C = new int[M];
		for (int i = 0; i < M; i++) {
			C[i] = in.nextInt();
		}
		int sum = 0;
		for (int i = 0; i < M; i++) {
			sum += C[i];
		}
		if (sum + (M + 1) * (D - 1) < N) {
			System.out.println("NO");
			return;
		}
		int[] A = new int[M + 1];
		int space = N - sum;
		for (int i = 0; i < M + 1 && space > 0; i++) {
			int x = Math.min(D - 1, space);
			A[i] = x;
			space -= x;
		}
		List<Integer> list = new ArrayList<>();
		for (int i = 0; i < M + 1; i++) {
			for (int j = 0; j < A[i]; j++) {
				list.add(0);
			}
			if (i < M) {
				for (int j = 0; j < C[i]; j++) {
					list.add(i + 1);
				}
			}
		}
		out.append("YES\n");
		for (int x : list) {
			out.append(x).append(' ');
		}
		out.setCharAt(out.length() - 1, '\n');

		System.out.print(out);
	}

	static class InputReader {
		public BufferedReader reader;
		public StringTokenizer st;

		public InputReader() {
			reader = new BufferedReader(new InputStreamReader(System.in));
		}

		public String next() {
			while (st == null || !st.hasMoreTokens()) {
				st = new StringTokenizer(nextLine());
			}
			return st.nextToken();
		}

		public String nextLine() {
			try {
				return reader.readLine();
			} catch (IOException e) {
				e.printStackTrace();
			}
			return null;
		}

		public int nextInt() {
			return Integer.parseInt(next());
		}
	}
}

