  cin.tie(NULL);
  int n, m, d;
  cin >> n >> m >> d;
  vector<int> plat(m);
  int total = 0;
  for (int i = 0; i < m; i++) {
    cin >> plat[i];
    total += plat[i];
  }
  if (n - total > (m + 1) * (d - 1)) {
    cout << "NO\n";
  } else {
    cout << "YES\n";
    int jump = 0;
    if (d == 1) {
      jump = n - total;
    } else {
      if ((n - total) % (d - 1) == 0) {
        jump = (n - total) / (d - 1);
      } else {
        jump = ((n - total) / (d - 1)) + 1;
      }
    }
    int together = (m + 1) - jump;
    int current_plat = 0;
    int index = 1;
    for (int i = 0; i < together && index <= n; i++) {
      index += plat[i];
      for (int j = 0; j < plat[i]; j++) {
        cout << i + 1 << " ";
      }
    }
    current_plat = together;
    for (int i = 0; i < jump; i++) {
      for (int i = 0; i < d - 1 && index <= n; i++) {
        cout << "0 ";
        index++;
      }
      if (index > n) {
        break;
      }
      for (int i = 0; i < plat[current_plat]; i++) {
        cout << current_plat + 1 << " ";
      }
      index += plat[current_plat];
      current_plat++;
    }
    cout << endl;
  }
