const int dy[4] = {-1, 1, 0, 0};
const int N = 1e3 + 1;
int n, m, d, c[N], mark[N], pos[N];
int main() {
  ios_base::sync_with_stdio(false);
  cin.tie(0);
  cout.tie(0);
  cin >> n >> m >> d;
  for (int i = 1; i <= m; i++) {
    cin >> c[i];
  }
  for (int i = m; i > 0; i--) {
    for (int j = n; j > 0; j--) {
      if (mark[j] == 0) {
        pos[i] = j - c[i] + 1;
        for (int x = pos[i]; x <= j; x++) mark[x] = i;
        break;
      }
    }
  }
  int last = 0;
  int cur = 1;
  for (int i = 0; i <= n; i++) {
    if (mark[i + 1] == 0) {
      if (cur > m) break;
      int mn = pos[cur];
      for (int j = pos[cur]; j <= pos[cur] + c[cur] - 1; j++) mark[j] = 0;
      mn = min(mn, i + d);
      for (int j = mn; j <= mn + c[cur] - 1; j++) mark[j] = cur;
      for (int j = i + 1; j < mn; j++) mark[j] = m + 1;
      last = max(last, mn + c[cur] - 1);
      cur++;
    }
    last = max(last, i);
  }
  if (n + 1 <= last + d) {
    cout << "YES\n";
    for (int i = 1; i <= n; i++)
      if (mark[i] == m + 1)
        cout << 0 << ' ';
      else
        cout << mark[i] << ' ';
  } else
    cout << "NO";
  return 0;
}

