    static class CPlatformsJumping {
        int[] c;
        int n;
        int m;
        int d;

        public void solve(int testNumber, InputReader in, PrintWriter out) {
            n = in.nextInt();
            m = in.nextInt();
            d = in.nextInt();
            c = in.nextIntArray(m);
            int[] result = ArrayUtils.createArray(n + 1, -1);
            int pt = 0;
            int where = 0;
            int was = n;

            for (int i = m - 1; i >= 0; --i) {
                for (int k = 0; k < c[i]; ++k) {
                    result[was--] = i;
                }
            }
            for (int pos = 1; pos <= n; ++pos) {
                if (pos - where > d - 1 && result[pos] == -1) {
                    int first = -1, which = -1;
                    for (int j = pos; j <= n; ++j) {
                        if (result[j] != -1) {
                            first = j;
                            which = result[j];
                            break;
                        }
                    }
                    if (first == -1) {
                        out.println("NO");
                        return;
                    }
                    int len = c[which];
                    for (int k = 0; k < len; ++k) {
                        result[k + first] = -1;
                        result[pos + k] = which;
                    }
                    where = pos + len - 1;
                }

            }
            out.println("YES");
            for (int i = 1; i <= n; ++i) {
                if (i > 1) out.print(" ");
                out.print(result[i] + 1);
            }
            out.println();
        }

    }

    static class InputReader {
        private InputStream stream;
        private static final int DEFAULT_BUFFER_SIZE = 1 << 16;
        private static final int EOF = -1;
        private byte[] buf = new byte[DEFAULT_BUFFER_SIZE];
        private int curChar;
        private int numChars;

        public InputReader(InputStream stream) {
            this.stream = stream;
        }

        public int read() {
            if (this.numChars == EOF) {
                throw new UnknownError();
            } else {
                if (this.curChar >= this.numChars) {
                    this.curChar = 0;

                    try {
                        this.numChars = this.stream.read(this.buf);
                    } catch (IOException ex) {
                        throw new InputMismatchException();
                    }

                    if (this.numChars <= 0) {
                        return EOF;
                    }
                }

                return this.buf[this.curChar++];
            }
        }

        public int nextInt() {
            int c;
            for (c = this.read(); isSpaceChar(c); c = this.read()) {
            }

            byte sgn = 1;
            if (c == 45) {
                sgn = -1;
                c = this.read();
            }

            int res = 0;

            while (c >= 48 && c <= 57) {
                res *= 10;
                res += c - 48;
                c = this.read();
                if (isSpaceChar(c)) {
                    return res * sgn;
                }
            }

            throw new InputMismatchException();
        }

        public static boolean isSpaceChar(int c) {
            return c == 32 || c == 10 || c == 13 || c == 9 || c == EOF;
        }

        public int[] nextIntArray(int n) {
            int[] arr = new int[n];
            for (int i = 0; i < n; i++) {
                arr[i] = nextInt();
            }
            return arr;
        }

    }

    static abstract class ArrayUtils {
        public static int[] createArray(int n, int value) {
            int[] res = new int[n];
            for (int i = 0; i < n; ++i) {
                res[i] = value;
            }
            return res;
        }

    }
}

