		// add the length of each of the platforms (assume that
		// between each platform you jump the maximum distance
		//if the total sum is at least n, then u can make it
		//huh i have to print the sequence, that's weird
		//just place them all d-1 distance apart, and 
		//incrementally move the platforms to the end
		//until the sum is less than n XD
		//then all the platforms in the end are consecutive
		//the rest are spaced d-1 along the beginning
		int riverWidth = sc.nextInt();
		int numPlatforms = sc.nextInt();
		int maxJump = sc.nextInt();

		int counter = 0;

		int[] platformSizes = new int[numPlatforms];
		for (int i=0;i<numPlatforms;i++) {
			int size = sc.nextInt();
			platformSizes[i] = size;
			counter += size;
		}
		if (counter + (maxJump-1)*(numPlatforms+1)<riverWidth) {
			System.out.println("NO");
		}
		else {
			System.out.println("YES");
		//it means u can make it across
		int difference = riverWidth - counter;
		int numJumps = 0;
		int lastJump = 0;
		boolean perfectJump = false;
		String finalPrint = "";
		String zeroString = repeatString(maxJump-1,"0");
		if (maxJump != 1) {

		int remainder = difference%(maxJump-1);

		if (remainder==0) {
			numJumps = difference/(maxJump-1);
			perfectJump = true;
		}
		else {
			numJumps = difference/(maxJump-1);
			lastJump = remainder;
		}
		}
		else {
			perfectJump = true;
			numJumps = 0;
		}
		if (perfectJump) {
			if (numJumps == numPlatforms + 1) {
			for (int j=1;j<=numJumps-1;j++) {
				finalPrint += zeroString;
				finalPrint += repeatString(platformSizes[j-1],Integer.toString(j));
			}
			for (int k=0;k<numPlatforms-numJumps;k++) {
				int kevin = k + numJumps;
				finalPrint += repeatString(platformSizes[kevin],Integer.toString(kevin+1));
			}
			finalPrint += zeroString;
			System.out.println(finalPrint);
			}
			else {
				for (int j=1;j<=numJumps;j++) {
