        if (ans[i] != 0) {
          lpos = i;
          break;
        }
      }
      if (lpos == -1) {
        cout << "NO" << endl;
        return 0;
      }
      int rpos = -1;
      for (int i = lpos; i < n + 2; ++i) {
        if (ans[i] == ans[lpos]) rpos = i;
      }
      while (ans[now + d] == 0) {
        swap(ans[lpos - 1], ans[rpos]);
        --lpos, --rpos;
      }
    }
    now += d;
  }
  cout << "YES" << endl;
  for (int i = 1; i <= n; ++i) {
    cout << ans[i] << " ";
  }
  cout << endl;
  return 0;
}

