public class Main {
    PrintStream so = System.out;
    long mod = 1000_000_000+7;
    String yes = "YES";
    String no = "NO";

    public static void main(String[] args) throws IOException {
        Main sol = new Main();
        int q = 1;// sol.parseInt();
        for(int i = 0; i<q; i++){
            sol.solve();
        }
    }

    void solve() throws IOException {
        int n = parseInt();
        int m = parseInt();
        int d = parseInt()-1;
        int[] c = parseIntArray(m);

        // solve
        int sum = 0;
        for(int cc : c) sum+=cc;
        if(sum+(m+1)*d<n){
            so.println(no);
            return;
        }
        int[] out = new int[n];
        int pos = 0;
        for(int i = 0; i<m; i++) {
            int max = (m-i +1)*d+ sum;
            int diff = max - (n-pos);
            // so.println(diff);
            if(diff < d){
                for(int j = 0; j<d-diff; j++) pos++;
            }
            for(int j = 0; j<c[i]; j++) out[pos++] = i+1;
            sum -= c[i];
        }

        so.println(yes);
        printArr(out);
    }




    /**
     * Helpers
     */
    int parseInt() throws IOException  { return Integer.parseInt(getNext()); }
    long parseLong() throws IOException  { return Long.parseLong(getNext()); }
    double parseDouble() throws IOException  { return Double.parseDouble(getNext()); }
    int[] parseIntArray(int len) throws IOException  { 
        int[] a = new int[len];
        for(int i =0; i<len;i++) a[i] = parseInt();
        return a;
    }
    long[] parseLongArray(int len) throws IOException { 
        long[] a = new long[len];
        for(int i =0; i<len;i++) a[i] = parseInt();
        return a;
    }

