public class R598C {

	public static void main(String[] args) {
		JS scan = new JS();
		PrintWriter out = new PrintWriter(System.out);
		int n = scan.nextInt();
		int m = scan.nextInt();
		int d = scan.nextInt();
		int[] arr = new int[m];
		for(int i = 0; i < m; i++) arr[i] = scan.nextInt();
		int[] ans = new int[n+1];
		int curr = d;
		boolean[] over = new boolean[m];
		for(int i = 0; i < m; i++) {
//			System.out.println(curr);
			int last = curr;
			for(int j = 0; j < arr[i]; j++) {
				if(curr+j > n) {
					over[i] = true;
					break;
				}
				ans[curr+j] = i+1;
				last = curr+j;
			}
			curr = last+d;
		}
//		System.out.println(curr);
		if(curr <= n) {
			System.out.println("NO");
			return;
		}
		out.println("YES");
		curr = n;
		for(int o = m-1; o >= 0; o--) {
			if(!over[o]) break;
			int last = 0;
			for(int j = 0; j < arr[o]; j++) {
				if(ans[curr-j] > 0)
					over[ans[curr-j]-1] = true;
				ans[curr-j] = o+1;
				last = curr-j;
			}
			curr = last-1;
		}
		for(int i = 1; i <= n; i++) { out.print(ans[i]+" ");

		}
		out.println();
		out.flush();
	}

	static class JS{
		public int BS = 1<<16;
		public char NC = (char)0;
		byte[] buf = new byte[BS];
		int bId = 0, size = 0;
		char c = NC;
		double num = 1;
		BufferedInputStream in;

		public JS() {
			in = new BufferedInputStream(System.in, BS);
		}

		public JS(String s) throws FileNotFoundException {
			in = new BufferedInputStream(new FileInputStream(new File(s)), BS);
		}

		public char nextChar(){
			while(bId==size) {
				try {
					size = in.read(buf);
				}catch(Exception e) {
					return NC;
				}				
				if(size==-1)return NC;
				bId=0;
			}
			return (char)buf[bId++];
		}

		public int nextInt() {
			return (int)nextLong();
		}

		public long nextLong() {
			num=1;
			boolean neg = false;
			if(c==NC)c=nextChar();
			for(;(c<'0' || c>'9'); c = nextChar()) {
				if(c=='-')neg=true;
			}
			long res = 0;
			for(; c>='0' && c <='9'; c=nextChar()) {
				res = (res<<3)+(res<<1)+c-'0';
				num*=10;
			}
			return neg?-res:res;
		}

		public double nextDouble() {
			double cur = nextLong();
			return c!='.' ? cur:cur+nextLong()/num;
		}

		public String next() {
			StringBuilder res = new StringBuilder();
			while(c<=32)c=nextChar();
			while(c>32) {
				res.append(c);
				c=nextChar();
			}
			return res.toString();
		}

		public String nextLine() {
			StringBuilder res = new StringBuilder();
			while(c<=32)c=nextChar();
			while(c!='\n') {
				res.append(c);
				c=nextChar();
			}
			return res.toString();
		}

		public boolean hasNext() {
			if(c>32)return true;
			while(true) {
				c=nextChar();
				if(c==NC)return false;
				else if(c>32)return true;
			}
		}
	}
}

