using namespace std;
int n, num_p, d, C[1111];
int p_st[1111];
void one_front(int ed) {
  if (p_st[1] > 1) p_st[1]--;
  for (int i = 2; i <= ed; ++i) {
    if (p_st[i - 1] + C[i - 1] <= p_st[i] - 1) p_st[i]--;
  }
}
int main(void) {
  scanf("%d%d%d", &n, &num_p, &d);
  for (int i = 1; i <= num_p; ++i) scanf("%d", &C[i]);
  p_st[1] = min(d, n - C[1] + 1);
  for (int i = 2; i <= num_p; ++i) {
    int can_nxt = p_st[i - 1] + C[i - 1];
    int nxt_st = can_nxt + d - 1;
    int nxt_ed = nxt_st + C[i] - 1;
    if (nxt_ed <= n) {
      p_st[i] = nxt_st;
      continue;
    }
    nxt_ed = n;
    nxt_st = n - C[i] + 1;
    if (can_nxt <= nxt_st) {
      p_st[i] = nxt_st;
      continue;
    }
    while (1) {
      one_front(i - 1);
      if (p_st[i - 1] + C[i - 1] <= n - C[i] + 1) {
        p_st[i] = n - C[i] + 1;
        break;
      }
    }
  }
  if (p_st[num_p] + C[num_p] - 1 + d < n + 1) {
    puts("NO");
    return 0;
  }
  vector<int> res(n + 1);
  for (int i = 1; i <= num_p; ++i) {
    for (int j = p_st[i]; j <= p_st[i] + C[i] - 1; ++j) res[j] = i;
  }
  puts("YES");
  for (int i = 1; i <= n; ++i) printf("%d ", res[i]);
  puts("");
  return 0;
}

