import java.io.BufferedReader;
import java.io.BufferedWriter;
import java.io.InputStreamReader;
import java.io.OutputStreamWriter;
import java.io.PrintWriter;

import java.util.*;

public class platform {
	public static void main(String[] args) throws Exception {
		InputStreamReader r = new InputStreamReader(System.in);
		BufferedReader br = new BufferedReader(r);
		PrintWriter pw = new PrintWriter(new BufferedWriter(
			new OutputStreamWriter(System.out)));

		String[] input = br.readLine().split(" ");
		int n = Integer.parseInt(input[0]);
		int m = Integer.parseInt(input[1]);
		int d = Integer.parseInt(input[2]);

		int remainingBoardDist = 0;
		LinkedList<Integer> lst = new LinkedList<>();

		String[] boards = br.readLine().split(" ");

		for (int i = 0; i < m; i++) {
			int boardWidth = Integer.parseInt(boards[i]);
			lst.add(boardWidth);
			remainingBoardDist += boardWidth;
		}

		int remainingLength = n;
		int myPos = 0;
		int boardCounter = 1;
		int[] boardArr = new int[n + 1];
		while (lst.size() > 0 && remainingBoardDist < remainingLength && remainingLength > 0) {
			myPos = Math.min(myPos + d, n - remainingBoardDist + 1);
			int length = lst.poll();
			remainingBoardDist -= length;
			for (int j = 0; j < length; j++) {
				boardArr[myPos + j] = boardCounter;
			}
			myPos += (length - 1);
			boardCounter++;
			remainingLength = n - myPos;
		}

		if (lst.size() > 0) {
			for (int b : lst) {
				for (int l = 0; l < b; l++) {
					myPos++;
					boardArr[myPos] = boardCounter;
				}
				boardCounter++;
			}
		} else {
			remainingLength = Math.max(remainingLength - d + 1, 0);
		}

		if (remainingLength == 0 || remainingLength == remainingBoardDist) {
			pw.println("YES");
			int counter = 0;
			for (int ele : boardArr) {
				if (counter == 0) counter++;
				else pw.print(ele + " ");
			}
			pw.println();
		}
		else pw.println("NO");

		pw.close();
	}
}

import java.io.OutputStream;
import java.io.IOException;
import java.io.InputStream;
import java.io.PrintWriter;
import java.util.Scanner;

/**
 * Built using CHelper plug-in
 * Actual solution is at the top
 */
public class Main
{
    public static void main(String[] args)
    {
        InputStream inputStream = System.in;
        OutputStream outputStream = System.out;
        Scanner in = new Scanner(inputStream);
        PrintWriter out = new PrintWriter(outputStream);
        TaskC solver = new TaskC();
        solver.solve(1, in, out);
        out.close();
    }

    static class TaskC
    {
        public void solve(int testNumber, Scanner in, PrintWriter out)
        {
            int n = in.nextInt();
            int m = in.nextInt();
            int d = in.nextInt();
            in.nextLine();
            String platformsString = in.nextLine();
            String[] platforms = platformsString.split(" ");
            int platformsNums[] = convert(platforms);

            int river[] = new int[n + 2];

            for (int i = m - 1, pos = n; i >= 0; --i)
            {
                for (int len = 0; len < platformsNums[i]; ++len)
                {
                    river[pos - len] = i + 1;
                }
                pos -= platformsNums[i];
            }

            int currentPos = 0;
            while (true)
            {
                while (currentPos + 1 < n + 1 && river[currentPos + 1] > 0) ++currentPos;

                if (currentPos + d >= n + 1) break;
                //needs a platform?
                if (river[currentPos + d] == 0)
                {
                    int lpos = -1;
                    //identify first platform
                    for (int i = currentPos + d; i < n + 2; ++i)
                    {
                        if (river[i] != 0)
                        {
                            lpos = i;
                            break;
                        }
                    }
                    // no more platforms
                    if (lpos == -1)
                    {
                        out.print("NO");
                        return;
                    }
                    //detect end of the current platform
                    int rpos = -1;
                    for (int i = lpos; i < n + 2; ++i)
                    {
                        if (river[i] == river[lpos]) rpos = i;
                    }
                    // keep shifting till getting the platform
                    while (river[currentPos + d] == 0)
                    {
                        //swap
                        int t = river[lpos - 1];
                        river[lpos - 1] = river[rpos];
                        river[rpos] = t;
                        --lpos;
                        --rpos;
                    }
                }
                currentPos += d;
            }
            out.println("YES");
            for (int i = 1; i <= n; i++)
            {
                out.print(river[i] + " ");
            }
        }

        public static int[] convert(String[] platforms)
        {
            int nums[] = new int[platforms.length];
            for (int i = 0; i < platforms.length; i++)
            {
                nums[i] = Integer.parseInt(platforms[i]);
            }
            return nums;
        }

    }
}

