int n, m, d;
int board[N], ans[N];
int main() {
  n = read(), m = read(), d = read();
  for (int i = 1; i <= m; i++) board[i] = read();
  for (int i = m, pos = n; i >= 1; i--) {
    for (int len = 1; len <= board[i]; len++) ans[pos--] = i;
  }
  int now = 0, x = 0;
  while (1) {
    x++;
    while (now + 1 < n + 1 && ans[now + 1] > 0) now++;
    if (now + d >= n + 1) break;
    if (ans[now + d] == 0) {
      int Nxt = -1, End = -1;
      for (int i = now + d; i <= n; i++)
        if (ans[i] != 0) {
          Nxt = i;
          break;
        }
      if (Nxt == -1) {
        puts("NO");
        return 0;
      }
      for (int i = Nxt; i <= n; i++) {
        if (ans[i] == ans[Nxt]) End = i;
      }
      for (int i = now + d, j = Nxt; j <= End; i++, j++) swap(ans[i], ans[j]);
    }
    now += d;
  }
  puts("YES");
  for (int i = 1; i <= n; i++) printf("%d ", ans[i]);
  puts("");
  return 0;
}

