public class Main {
	public static void main(String args[]) {new Main().run();}

	FastReader in = new FastReader();
	PrintWriter out = new PrintWriter(System.out);
	void run(){
		work();
		out.flush();
	}
	long mod=1000000007;
	long gcd(long a,long b) {
		return b==0?a:gcd(b,a%b);
	}

	long ret=0;
	void work() {
		int n=in.nextInt();
		int m=in.nextInt();
		int d=in.nextInt();
		int[] A=new int[m+1];
		int sum=1;
		for(int i=0;i<m;i++) {
			A[i]=in.nextInt();
			sum+=A[i];
		}
		A[m]=1;
		if(sum+(m+1)*(d-1)<n+1) {
			out.println("NO");
			return;
		}
		int[] ret=new int[n+1];
		int step=(n+1-sum)/(m+1);
		int c2=(n+1-sum)%(m+1);
		int c1=(m+1)-c2;
		for(int i=0,index=1;c1>0||c2>0;index++) {
			if(c2>0) {
				c2--;
				for(int j=0;j<step+1;j++,i++);
				int j=i;
				for(;j<i+A[index-1];j++) {
					ret[j]=index;
				}
				i=j;
			}else {
				c1--;
				for(int j=0;j<step;j++,i++);
				int j=i;
				for(;j<i+A[index-1];j++) {
					ret[j]=index;
				}
				i=j;
			}
		}
		out.println("YES");
		for(int i=0;i<n;i++) {
			out.print(ret[i]+" ");
		}
	}
}



