        int d=fs.nextInt();
        int ptr=0;
        int c[]=new int[m];
        int sum=0;
        for(int i=0;i<m;i++)
        {
            c[i]=fs.nextInt();
            sum+=c[i];
        }
        if(sum+(m+1)*(d-1)<n)
        pw.println("NO");
